sys_script_include.script:
function validateEmailAddress(emailStr){
    // JavaScriptの強制を使用して、emailStrが文字列であることを保証
    emailStr = emailStr + '';
    // emailStrを、正規表現で指定されている許可された構文と比較
    // emailStrが構文を許可している場合は、trueを返し、そうでない場合はfalse
    if(emailStr.match(/^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/)){
      return true;
    }
    else {
     return false;
    }
  }
